use thiserror::Error;

/// Errors generated by the driver.
#[derive(Debug, Error)]
pub enum Error {
    #[cfg(feature = "gg20")]
    /// GG20 driver errors.
    #[error(transparent)]
    GG20(#[from] crate::gg20::Error),

    /// Client library errors.
    #[error(transparent)]
    Client(#[from] mpc_relay_client::Error),
}
